import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import PetList from "./components/PetList";
import PetDetails from "./components/PetDetails";
import Login from "./components/Login";
import Register from "./components/Register";
import "./styles/Homepage.css"; // Ana sayfa i√ßin CSS dosyasƒ±nƒ± dahil ettik

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Kullanƒ±cƒ± oturumunu kontrol et
  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token); // Eƒüer token varsa giri≈ü yapƒ±lmƒ±≈ü demektir
  }, []);

  return (
    <Router>
      <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />
      <Routes>
        <Route
          path="/"
          element={
            <div className="homepage">
              <div className="banner">
                <h1 className="banner-title">Welcome to Pawfect Care üêæ</h1>
                <p className="banner-subtitle">Your ultimate solution for managing your pets!</p>
              </div>
              <div className="about-section">
                <h2>Why Choose Us?</h2>
                <p>
                  At Pawfect Care, we ensure that your pets are happy and healthy.
                  From training to boarding, we provide all services to keep your furry friends safe.
                </p>
              </div>
            </div>
          }
        />
        <Route path="/login" element={isLoggedIn ? <Navigate to="/pets" /> : <Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/register" element={isLoggedIn ? <Navigate to="/pets" /> : <Register />} />
        <Route path="/pets" element={isLoggedIn ? <PetList /> : <Navigate to="/login" />} />
        <Route path="/pets/:id" element={isLoggedIn ? <PetDetails /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
