import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../services/api";
import "./PetDetails.css"; // Stil dosyasını entegre edin

const PetDetails = () => {
  const { id } = useParams(); // URL'den ID'yi alın
  const [pet, setPet] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPetDetails = async () => {
      try {
        const { data } = await API.get(`/pets/${id}`); // Backend'den pet'i çek
        setPet(data);
      } catch (err) {
        console.error("Error fetching pet details:", err);
        setError("Could not fetch pet details. Please try again later.");
      }
    };

    fetchPetDetails();
  }, [id]);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!pet) {
    return <div className="loading-message">Loading pet details...</div>;
  }

  return (
    <div className="pet-details-container">
      <h2>{pet.name}'s Details</h2>
      <p><strong>Species:</strong> {pet.species}</p>
      <p><strong>Birth Date:</strong> {new Date(pet.birthDate).toLocaleDateString()}</p>
      {pet.vaccines && (
        <div>
          <h3>Vaccines:</h3>
          <ul>
            {pet.vaccines.map((vaccine, index) => (
              <li key={index}>{vaccine}</li>
            ))}
          </ul>
        </div>
      )}
      {pet.healthRecords && (
        <div>
          <h3>Health Records:</h3>
          <ul>
            {pet.healthRecords.map((record, index) => (
              <li key={index}>
                {record.date}: {record.notes}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default PetDetails;
