import React from "react";
import Particles from "react-tsparticles";

const AnimatedBackground = () => {
  return (
    <Particles
      id="tsparticles"
      options={{
        background: {
          color: {
            value: "#f8e8e8",
          },
        },
        particles: {
          number: {
            value: 80,
            density: {
              enable: true,
              value_area: 800,
            },
          },
          color: {
            value: "#ff7b7b",
          },
          shape: {
            type: "circle",
          },
          opacity: {
            value: 0.6,
            random: true,
          },
          size: {
            value: 3,
            random: true,
          },
          move: {
            enable: true,
            speed: 2,
            random: false,
            straight: false,
            bounce: false,
          },
        },
      }}
    />
  );
};

export default AnimatedBackground;
