import React, { useState, useEffect } from "react";
import API from "../services/api";
import PetForm from "./PetForm";
import PetPhotoUpload from "./PetPhotoUpload";

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);

  // Pet listesini yükle
  useEffect(() => {
    const fetchPets = async () => {
      try {
        const { data } = await API.get("/pets");
        setPets(data);
      } catch (error) {
        console.error("Error fetching pets:", error);
      }
    };
    fetchPets();
  }, []);

  // Pet silme işlemi
  const handleDelete = async (id) => {
    try {
      await API.delete(`/pets/${id}`);
      setPets(pets.filter((pet) => pet._id !== id)); // Listeden kaldır
      alert("Pet deleted successfully!");
    } catch (error) {
      console.error("Error deleting pet:", error);
    }
  };

  return (
    <div className="container">
      <h2>My Pets</h2>

      {/* Pet Ekleme/Güncelleme Formu */}
      <PetForm
        selectedPet={selectedPet}
        setSelectedPet={setSelectedPet}
        setPets={setPets}
        pets={pets}
      />

      {/* Pet Listesi */}
      <ul className="list-group mt-4">
        {pets.map((pet) => (
          <li
            key={pet._id}
            className="list-group-item d-flex flex-column justify-content-between align-items-start"
          >
            <div className="d-flex align-items-center">
              {pet.photoUrl && (
                <img
                  src={pet.photoUrl}
                  alt={pet.name}
                  style={{
                    width: "50px",
                    height: "50px",
                    marginRight: "10px",
                    borderRadius: "50%",
                  }}
                />
              )}
              <div>
                <strong>{pet.name}</strong> - {pet.species}
              </div>
            </div>

            <div className="d-flex justify-content-between mt-2">
              <button
                className="btn btn-warning btn-sm me-2"
                onClick={() => setSelectedPet(pet)}
              >
                Edit
              </button>
              <button
                className="btn btn-danger btn-sm"
                onClick={() => handleDelete(pet._id)}
              >
                Delete
              </button>
            </div>

            {/* Fotoğraf Yükleme Formu */}
            <PetPhotoUpload petId={pet._id} setPets={setPets} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PetList;
