import React from "react";

const PetDetails = ({ pet }) => {
  if (!pet) {
    return <p>No pet selected. Please choose a pet to see details.</p>;
  }

  return (
    <div className="pet-details">
      <h2>{pet.name}</h2>
      <p><strong>Species:</strong> {pet.species}</p>
      <p><strong>Birth Date:</strong> {pet.birthDate}</p>
      <p><strong>Additional Notes:</strong> {pet.notes || "No additional notes available."}</p>
      <img src={pet.photo || "/default-pet-image.jpg"} alt={pet.name} className="pet-photo" />
    </div>
  );
};

export default PetDetails;
