import React, { useState, useEffect } from "react";
import API from "../services/api";

const PetForm = ({ selectedPet, setSelectedPet, setPets, pets }) => {
  const [name, setName] = useState("");
  const [species, setSpecies] = useState("");
  const [birthDate, setBirthDate] = useState("");
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    if (selectedPet) {
      setName(selectedPet.name);
      setSpecies(selectedPet.species);
      setBirthDate(selectedPet.birthDate?.split("T")[0] || "");
    } else {
      setName("");
      setSpecies("");
      setBirthDate("");
      setPhoto(null);
    }
  }, [selectedPet]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedPet) {
        // Pet güncelleme
        const { data } = await API.put(`/pets/${selectedPet._id}`, {
          name,
          species,
          birthDate,
        });
        if (photo) {
          // Fotoğraf yükleme
          const formData = new FormData();
          formData.append("photo", photo);
          const photoResponse = await API.post(`/pets/${selectedPet._id}/photo`, formData, {
            headers: {
              "Content-Type": "multipart/form-data",
            },
          });
          data.photoUrl = photoResponse.data.photoUrl;
        }
        setPets(pets.map((pet) => (pet._id === selectedPet._id ? data : pet)));
        alert("Pet updated successfully!");
      } else {
        // Yeni pet ekleme
        const { data } = await API.post("/pets", {
          name,
          species,
          birthDate,
        });
        if (photo) {
          // Fotoğraf yükleme
          const formData = new FormData();
          formData.append("photo", photo);
          const photoResponse = await API.post(`/pets/${data._id}/photo`, formData, {
            headers: {
              "Content-Type": "multipart/form-data",
            },
          });
          data.photoUrl = photoResponse.data.photoUrl;
        }
        setPets([...pets, data]);
        alert("Pet added successfully!");
      }
      setSelectedPet(null);
      setName("");
      setSpecies("");
      setBirthDate("");
      setPhoto(null);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4">
      <h3>{selectedPet ? "Edit Pet" : "Add New Pet"}</h3>
      <div className="mb-3">
        <label htmlFor="name" className="form-label">
          Name
        </label>
        <input
          type="text"
          className="form-control"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="species" className="form-label">
          Species
        </label>
        <input
          type="text"
          className="form-control"
          id="species"
          value={species}
          onChange={(e) => setSpecies(e.target.value)}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="birthDate" className="form-label">
          Birth Date
        </label>
        <input
          type="date"
          className="form-control"
          id="birthDate"
          value={birthDate}
          onChange={(e) => setBirthDate(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="photo" className="form-label">
          Photo
        </label>
        <input
          type="file"
          className="form-control"
          id="photo"
          onChange={(e) => setPhoto(e.target.files[0])}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        {selectedPet ? "Update Pet" : "Add Pet"}
      </button>
      {selectedPet && (
        <button
          type="button"
          className="btn btn-secondary ms-2"
          onClick={() => setSelectedPet(null)}
        >
          Cancel
        </button>
      )}
    </form>
  );
};

export default PetForm;
