import React, { useState, useEffect } from "react";
import API from "../services/api";

const PetForm = ({ selectedPet, setSelectedPet, setPets, pets }) => {
  const [name, setName] = useState("");
  const [species, setSpecies] = useState("");
  const [birthDate, setBirthDate] = useState("");

  useEffect(() => {
    if (selectedPet) {
      setName(selectedPet.name);
      setSpecies(selectedPet.species);
      setBirthDate(selectedPet.birthDate?.split("T")[0] || "");
    } else {
      setName("");
      setSpecies("");
      setBirthDate("");
    }
  }, [selectedPet]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedPet) {
        const { data } = await API.put(`/pets/${selectedPet._id}`, {
          name,
          species,
          birthDate,
        });
        setPets(
          pets.map((pet) =>
            pet._id === selectedPet._id ? data : pet
          )
        );
        alert("Pet updated successfully!");
      } else {
        const { data } = await API.post("/pets", {
          name,
          species,
          birthDate,
        });
        setPets([...pets, data]);
        alert("Pet added successfully!");
      }
      setSelectedPet(null);
      setName("");
      setSpecies("");
      setBirthDate("");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white p-6 shadow-md rounded-lg mb-4"
    >
      <h3 className="text-xl font-bold mb-4">
        {selectedPet ? "Edit Pet" : "Add New Pet"}
      </h3>
      <div className="mb-3">
        <label htmlFor="name" className="block text-gray-700">
          Name
        </label>
        <input
          type="text"
          className="w-full border border-gray-300 rounded px-3 py-2"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="species" className="block text-gray-700">
          Species
        </label>
        <input
          type="text"
          className="w-full border border-gray-300 rounded px-3 py-2"
          id="species"
          value={species}
          onChange={(e) => setSpecies(e.target.value)}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="birthDate" className="block text-gray-700">
          Birth Date
        </label>
        <input
          type="date"
          className="w-full border border-gray-300 rounded px-3 py-2"
          id="birthDate"
          value={birthDate}
          onChange={(e) => setBirthDate(e.target.value)}
        />
      </div>
      <button
        type="submit"
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        {selectedPet ? "Update Pet" : "Add Pet"}
      </button>
      {selectedPet && (
        <button
          type="button"
          className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 ml-2"
          onClick={() => setSelectedPet(null)}
        >
          Cancel
        </button>
      )}
    </form>
  );
};

export default PetForm;
