import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token"); // Token'ı sil
    setIsLoggedIn(false); // Oturum durumunu güncelle
    navigate("/login"); // Giriş sayfasına yönlendir
  };

  return (
    <nav className="bg-blue-600 p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link className="text-white text-lg font-bold" to="/">
          Pet Care
        </Link>
        <div className="flex space-x-4">
          {!isLoggedIn && (
            <>
              <Link className="text-white hover:underline" to="/login">
                Login
              </Link>
              <Link className="text-white hover:underline" to="/register">
                Register
              </Link>
            </>
          )}
          {isLoggedIn && (
            <>
              <Link className="text-white hover:underline" to="/pets">
                My Pets
              </Link>
              <button
                className="text-white hover:underline"
                onClick={handleLogout}
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
