import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import API from "../services/api";
import "./PetDetails.css";

const PetDetails = () => {
  const { id } = useParams(); // URL'den pet ID'sini al
  const [pet, setPet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [appointments, setAppointments] = useState([]);
  const [weightHistory, setWeightHistory] = useState([]);
  const [newHealthRecord, setNewHealthRecord] = useState({ date: "", notes: "" });

  useEffect(() => {
    // Pet detaylarını yükleme
    const fetchPetDetails = async () => {
      try {
        const { data } = await API.get(`/pets/${id}`);
        setPet(data);
      } catch (err) {
        setError("Failed to load pet details. Please try again.");
        console.error("Error fetching pet details:", err);
      } finally {
        setLoading(false);
      }
    };

    // Randevuları yükleme
    const fetchAppointments = async () => {
      try {
        const { data } = await API.get(`/pets/${id}/appointments`);
        setAppointments(data);
      } catch (err) {
        console.error("Error fetching appointments:", err);
      }
    };

    // Ağırlık geçmişini yükleme
    const fetchWeightHistory = async () => {
      try {
        const { data } = await API.get(`/pets/${id}/weight`);
        setWeightHistory(data);
      } catch (err) {
        console.error("Error fetching weight history:", err);
      }
    };

    fetchPetDetails();
    fetchAppointments();
    fetchWeightHistory();
  }, [id]);

  const handleAddHealthRecord = async (e) => {
    e.preventDefault();
    try {
      const { data } = await API.post(`/pets/${id}/health-records`, newHealthRecord);
      setPet({ ...pet, healthRecords: data }); // Yeni sağlık kaydını güncelle
      setNewHealthRecord({ date: "", notes: "" }); // Formu sıfırla
    } catch (err) {
      console.error("Error adding health record:", err);
      setError("Failed to add health record. Please try again.");
    }
  };

  if (loading) {
    return <p className="loading-message">Loading pet details...</p>;
  }

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!pet) {
    return <p className="no-pet-message">No pet found. Please try again.</p>;
  }

  return (
    <div className="pet-details">
      <h2>{pet.name}'s Details</h2>
      <img src={pet.photo || "/default-pet-image.jpg"} alt={pet.name} className="pet-photo" />
      <p><strong>Species:</strong> {pet.species}</p>
      <p><strong>Birth Date:</strong> {new Date(pet.birthDate).toLocaleDateString()}</p>
      <p><strong>Notes:</strong> {pet.notes || "No additional notes available."}</p>

      {pet.dietPlan && (
        <div>
          <h3>Diet Plan:</h3>
          <p><strong>Food Type:</strong> {pet.dietPlan.foodType}</p>
          <p><strong>Portion Size:</strong> {pet.dietPlan.portionSize}</p>
          <p><strong>Frequency:</strong> {pet.dietPlan.frequency}</p>
        </div>
      )}

      {appointments && appointments.length > 0 && (
        <div>
          <h3>Appointments:</h3>
          <ul>
            {appointments.map((appointment, index) => (
              <li key={index}>
                {new Date(appointment.date).toLocaleDateString()}: {appointment.purpose} ({appointment.status})
              </li>
            ))}
          </ul>
        </div>
      )}

      {weightHistory && weightHistory.length > 0 && (
        <div>
          <h3>Weight History:</h3>
          <ul>
            {weightHistory.map((weight, index) => (
              <li key={index}>
                {new Date(weight.date).toLocaleDateString()}: {weight.value} kg
              </li>
            ))}
          </ul>
        </div>
      )}

      {pet.healthRecords && pet.healthRecords.length > 0 && (
        <div>
          <h3>Health Records:</h3>
          <ul>
            {pet.healthRecords.map((record, index) => (
              <li key={index}>
                {new Date(record.date).toLocaleDateString()}: {record.notes}
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="add-health-record">
        <h3>Add New Health Record</h3>
        <form onSubmit={handleAddHealthRecord}>
          <input
            type="date"
            value={newHealthRecord.date}
            onChange={(e) => setNewHealthRecord({ ...newHealthRecord, date: e.target.value })}
            required
          />
          <textarea
            value={newHealthRecord.notes}
            onChange={(e) => setNewHealthRecord({ ...newHealthRecord, notes: e.target.value })}
            placeholder="Enter health notes"
            required
          ></textarea>
          <button type="submit">Add Record</button>
        </form>
      </div>
    </div>
  );
};

export default PetDetails;
