import React, { useState, useEffect } from "react";
import API from "../services/api";

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    const fetchPets = async () => {
      try {
        const { data } = await API.get("/pets");
        setPets(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchPets();
  }, []);

  const handlePhotoUpload = async (petId) => {
    if (!photo) return alert("Please select a photo to upload!");

    const formData = new FormData();
    formData.append("photo", photo);

    try {
      const { data } = await API.post(`/pets/${petId}/photo`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setPets(
        pets.map((pet) =>
          pet._id === petId ? { ...pet, photoUrl: data.photoUrl } : pet
        )
      );
      alert("Photo uploaded successfully!");
      setPhoto(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/pets/${id}`);
      setPets(pets.filter((pet) => pet._id !== id));
      alert("Pet deleted successfully!");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container">
      <h2>My Pets</h2>
      <ul className="list-group">
        {pets.map((pet) => (
          <li key={pet._id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <strong>{pet.name}</strong> - {pet.species}
              {pet.photoUrl && (
                <img
                  src={pet.photoUrl}
                  alt={pet.name}
                  style={{ width: "100px", height: "100px", objectFit: "cover", marginLeft: "10px" }}
                />
              )}
            </div>
            <div>
              <input
                type="file"
                onChange={(e) => setPhoto(e.target.files[0])}
                style={{ display: "block", marginBottom: "10px" }}
              />
              <button
                className="btn btn-primary btn-sm"
                onClick={() => handlePhotoUpload(pet._id)}
              >
                Upload Photo
              </button>
              <button
                className="btn btn-danger btn-sm ms-2"
                onClick={() => handleDelete(pet._id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PetList;
