import React, { useState } from "react";
import API from "../services/api";

const PetPhotoUpload = ({ petId, setPets }) => {
  const [photo, setPhoto] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!photo) return alert("Please select a photo!");

    const formData = new FormData();
    formData.append("photo", photo);

    try {
      const { data } = await API.post(`/pets/${petId}/photo`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Photo uploaded successfully!");
      setPets((prevPets) =>
        prevPets.map((pet) => (pet._id === petId ? { ...pet, photoUrl: data.photoUrl } : pet))
      );
    } catch (error) {
      console.error(error);
      alert("Photo upload failed. Please try again.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mt-3">
      <label htmlFor="photo" className="form-label">
        Upload Photo
      </label>
      <input
        type="file"
        className="form-control"
        id="photo"
        onChange={(e) => setPhoto(e.target.files[0])}
        required
      />
      <button type="submit" className="btn btn-primary mt-2">
        Upload
      </button>
    </form>
  );
};

export default PetPhotoUpload;
