import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import PetList from "./components/PetList";
import PetDetails from "./components/PetDetails";
import BlogList from "./components/BlogList"; // Blog listesi i√ßin component
import BlogDetails from "./components/BlogDetails"; // Blog detaylarƒ± i√ßin component
import BlogEditForm from "./components/BlogEditForm"; // Blog d√ºzenleme formu i√ßin component
import AboutUs from "./components/AboutUs"; // About Us (Hakkƒ±mƒ±zda) componenti
import Login from "./components/Login";
import Register from "./components/Register";
import "./styles/Homepage.css";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Kullanƒ±cƒ± oturumunu kontrol et
  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token); // Eƒüer token varsa giri≈ü yapƒ±lmƒ±≈ü demektir
  }, []);

  return (
    <Router>
      <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />
      <Routes>
        <Route
          path="/"
          element={
            <div className="homepage">
              <div className="banner">
                <h1 className="banner-title">Pawfect Care'a Hosgeldiniz üêæ</h1>
                <p className="banner-subtitle">Evcil hayvanlarƒ±nƒ±zƒ±n y√∂netimi i√ßin nihai √ß√∂z√ºm√ºn√ºz!</p>
              </div>
              <div className="about-section">
                <h2>Neden Bizi Se√ßmelisiniz?</h2>
                <p>
                  Pawfect Care'de evcil hayvanlarƒ±nƒ±zƒ±n mutlu ve saƒülƒ±klƒ± olmasƒ±nƒ± saƒülƒ±yoruz.
                  Evcil dostlarƒ±mƒ±zƒ±n ihmal edilmelerinin √∂n√ºne ge√ßiyoruz.
                  Sosyal ya≈üantƒ±nƒ±zla ili≈ükilendirme fƒ±rsatƒ± sunuyoruz.
                </p>
              </div>
            </div>
          }
        />
        <Route path="/login" element={isLoggedIn ? <Navigate to="/pets" /> : <Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/register" element={isLoggedIn ? <Navigate to="/pets" /> : <Register />} />
        <Route path="/pets" element={isLoggedIn ? <PetList /> : <Navigate to="/login" />} />
        <Route path="/pets/:id" element={isLoggedIn ? <PetDetails /> : <Navigate to="/login" />} />
        <Route path="/blogs" element={<BlogList />} /> {/* Blog listesi rotasƒ± */}
        <Route path="/blogs/:id" element={<BlogDetails />} /> {/* Blog detaylarƒ± rotasƒ± */}
        <Route path="/blogs/edit/:id" element={<BlogEditForm />} /> {/* Blog d√ºzenleme rotasƒ± */}
        <Route path="/about-us" element={<AboutUs />} /> {/* About Us rotasƒ± */}
      </Routes>
    </Router>
  );
};

export default App;
