import React, { useState } from "react";
import API from "../services/api";

const PetPhotoUpload = ({ petId, onPhotoUploaded }) => {
  const [photo, setPhoto] = useState(null);
  const [uploading, setUploading] = useState(false);

  const handlePhotoChange = (e) => {
    setPhoto(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!photo) {
      alert("Please select a photo to upload!");
      return;
    }
    const formData = new FormData();
    formData.append("photo", photo);

    try {
      setUploading(true);
      const { data } = await API.post(`/pets/${petId}/photo`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      alert("Photo uploaded successfully!");
      onPhotoUploaded(data.photoUrl); // Yeni fotoğraf URL'sini üst bileşene gönder
    } catch (error) {
      console.error(error);
      alert("Photo upload failed!");
    } finally {
      setUploading(false);
      setPhoto(null); // Formu sıfırla
    }
  };

  return (
    <div>
      <form onSubmit={handleUpload}>
        <div className="mb-3">
          <label htmlFor="photo" className="form-label">
            Upload Photo
          </label>
          <input
            type="file"
            className="form-control"
            id="photo"
            onChange={handlePhotoChange}
            accept="image/*"
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={uploading}>
          {uploading ? "Uploading..." : "Upload"}
        </button>
      </form>
    </div>
  );
};

export default PetPhotoUpload;
