import React, { useState, useEffect } from "react";
import API from "../services/api";

const PetList = () => {
  const [pets, setPets] = useState([]);

  useEffect(() => {
    const fetchPets = async () => {
      try {
        const { data } = await API.get("/pets");
        setPets(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchPets();
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">My Pets</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {pets.map((pet) => (
          <div
            key={pet._id}
            className="bg-white p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow"
          >
            <h3 className="text-xl font-bold text-gray-700">{pet.name}</h3>
            <p className="text-gray-600">Species: {pet.species}</p>
            <p className="text-gray-600">
              Birth Date: {new Date(pet.birthDate).toLocaleDateString()}
            </p>
            <button className="mt-4 bg-primary text-gray-800 py-1 px-3 rounded-lg shadow hover:bg-accent transition">
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PetList;
