import React, { useState, useEffect } from "react";
import API from "../services/api";
import "./PetForm.css";

const PetForm = ({ selectedPet, setSelectedPet, setPets, pets }) => {
  const [formData, setFormData] = useState({
    name: "",
    species: "",
    gender: "",
    birthDate: "",
    photoUrl: "",
    notes: "",
    weight: ""
  });

  useEffect(() => {
    if (selectedPet) {
      setFormData({
        name: selectedPet.name,
        species: selectedPet.species,
        gender: selectedPet.gender || "",
        birthDate: selectedPet.birthDate?.split("T")[0] || "",
        photoUrl: selectedPet.photoUrl || "",
        notes: selectedPet.notes || "",
        weight: ""
      });
    }
  }, [selectedPet]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...formData };
      if (formData.weight) {
        payload.weightHistory = [{ weight: parseFloat(formData.weight), date: new Date() }];
      }

      if (selectedPet) {
        const { data } = await API.put(`/pets/${selectedPet._id}`, payload);
        setPets(pets.map((pet) => pet._id === selectedPet._id ? data : pet));
      } else {
        const { data } = await API.post("/pets", payload);
        setPets([...pets, data]);
      }
      
      setSelectedPet(null);
      setFormData({
        name: "", species: "", gender: "", birthDate: "",
        photoUrl: "", notes: "", weight: ""
      });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4">
      <h3>{selectedPet ? "Edit Pet" : "Add New Pet"}</h3>
      
      <div className="mb-3">
        <label>Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="form-control"
          required
        />
      </div>

      <div className="mb-3">
        <label>Species</label>
        <input
          type="text"
          name="species"
          value={formData.species}
          onChange={handleChange}
          className="form-control"
          required
        />
      </div>

      <div className="mb-3">
        <label>Gender</label>
        <select
          name="gender"
          value={formData.gender}
          onChange={handleChange}
          className="form-control"
          required
        >
          <option value="">Select Gender</option>
          <option value="erkek">Erkek</option>
          <option value="dişi">Dişi</option>
        </select>
      </div>

      <div className="mb-3">
        <label>Birth Date</label>
        <input
          type="date"
          name="birthDate"
          value={formData.birthDate}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      <div className="mb-3">
        <label>Photo URL</label>
        <input
          type="text"
          name="photoUrl"
          value={formData.photoUrl}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      <div className="mb-3">
        <label>Weight (kg)</label>
        <input
          type="number"
          name="weight"
          value={formData.weight}
          onChange={handleChange}
          className="form-control"
          step="0.1"
        />
      </div>

      <div className="mb-3">
        <label>Notes</label>
        <textarea
          name="notes"
          value={formData.notes}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      <button type="submit" className="btn btn-primary">
        {selectedPet ? "Update Pet" : "Add Pet"}
      </button>
      {selectedPet && (
        <button
          type="button"
          className="btn btn-secondary ms-2"
          onClick={() => setSelectedPet(null)}
        >
          Cancel
        </button>
      )}
    </form>
  );
};

export default PetForm;
