import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import API from "../services/api";
import "./PetDetails.css";

const PetDetails = () => {
  const { id } = useParams();
  const [pet, setPet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newHealthRecord, setNewHealthRecord] = useState({ date: "", notes: "" });
  const [newWeight, setNewWeight] = useState({ weight: "", notes: "" });

  useEffect(() => {
    const fetchPetDetails = async () => {
      try {
        const { data } = await API.get(`/pets/${id}`);
        setPet(data);
      } catch (err) {
        setError("Failed to load pet details.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPetDetails();
  }, [id]);

  const handleAddHealthRecord = async (e) => {
    e.preventDefault();
    try {
      const { data } = await API.post(`/pets/${id}/health-records`, newHealthRecord);
      setPet(data);
      setNewHealthRecord({ date: "", notes: "" });
    } catch (err) {
      setError("Failed to add health record.");
    }
  };

  const handleAddWeight = async (e) => {
    e.preventDefault();
    try {
      const { data } = await API.post(`/pets/${id}/weight`, newWeight);
      setPet(data);
      setNewWeight({ weight: "", notes: "" });
    } catch (err) {
      setError("Failed to add weight record.");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!pet) return <p>Pet not found.</p>;

  const getUpcomingVaccines = () => {
    if (!pet.vaccines) return [];
    const today = new Date();
    return pet.vaccines.filter(v => {
      const nextDate = new Date(v.nextVaccineDate);
      return nextDate > today && nextDate <= new Date(today.setDate(today.getDate() + 30));
    });
  };

  return (
    <div className="pet-details">
      <h2>{pet.name}'s Details</h2>
      
      <div className="basic-info">
        <img src={pet.photoUrl || "/default-pet-image.jpg"} alt={pet.name} className="pet-photo" />
        <p><strong>Species:</strong> {pet.species}</p>
        <p><strong>Gender:</strong> {pet.gender}</p>
        <p><strong>Birth Date:</strong> {new Date(pet.birthDate).toLocaleDateString()}</p>
        <p><strong>Notes:</strong> {pet.notes || "No notes"}</p>
      </div>

      <div className="weight-history">
        <h3>Weight History</h3>
        {pet.weightHistory?.map((w, i) => (
          <div key={i}>
            {new Date(w.date).toLocaleDateString()}: {w.weight}kg {w.notes && `- ${w.notes}`}
          </div>
        ))}
        
        <form onSubmit={handleAddWeight} className="add-weight-form">
          <input
            type="number"
            step="0.1"
            value={newWeight.weight}
            onChange={(e) => setNewWeight({...newWeight, weight: e.target.value})}
            placeholder="Weight (kg)"
            required
          />
          <input
            type="text"
            value={newWeight.notes}
            onChange={(e) => setNewWeight({...newWeight, notes: e.target.value})}
            placeholder="Notes (optional)"
          />
          <button type="submit">Add Weight</button>
        </form>
      </div>

      <div className="vaccines">
        <h3>Vaccines</h3>
        {pet.vaccines?.map((v, i) => (
          <div key={i}>
            {v.vaccineName} - Date: {new Date(v.vaccineDate).toLocaleDateString()}
            {v.nextVaccineDate && ` - Next: ${new Date(v.nextVaccineDate).toLocaleDateString()}`}
          </div>
        ))}
      </div>

      <div className="upcoming-vaccines">
        <h3>Upcoming Vaccines</h3>
        {getUpcomingVaccines().map((v, i) => (
          <div key={i} className="vaccine-reminder">
            {v.vaccineName} due on {new Date(v.nextVaccineDate).toLocaleDateString()}
          </div>
        ))}
      </div>

      <div className="health-records">
        <h3>Health Records</h3>
        {pet.healthRecords?.map((record, index) => (
          <div key={index}>
            {new Date(record.date).toLocaleDateString()}: {record.notes}
          </div>
        ))}
        
        <form onSubmit={handleAddHealthRecord} className="add-health-record">
          <input
            type="date"
            value={newHealthRecord.date}
            onChange={(e) => setNewHealthRecord({...newHealthRecord, date: e.target.value})}
            required
          />
          <textarea
            value={newHealthRecord.notes}
            onChange={(e) => setNewHealthRecord({...newHealthRecord, notes: e.target.value})}
            placeholder="Health notes"
            required
          />
          <button type="submit">Add Record</button>
        </form>
      </div>
    </div>
  );
};

export default PetDetails;
