import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import API from "../services/api";
import "./PetDetails.css";

const PetDetails = () => {
  const { id } = useParams();
  const [pet, setPet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Dinamik olarak formlar için state
  const [formState, setFormState] = useState({
    healthRecord: { date: "", notes: "" },
    weight: { date: "", weight: "" },
    medication: { name: "", dosage: "", frequency: "", startDate: "", endDate: "", notes: "" },
    appointment: { date: "", time: "", reason: "", location: "" },
    emergencyContact: { name: "", relationship: "", phone: "", address: "" },
  });

  useEffect(() => {
    const fetchPetDetails = async () => {
      try {
        const { data } = await API.get(`/pets/${id}`);
        setPet(data);
      } catch (err) {
        setError("Failed to load pet details. Please try again.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchPetDetails();
  }, [id]);

  const handleAddRecord = async (type, endpoint, data) => {
    try {
      const response = await API.post(`/pets/${id}/${endpoint}`, data);
      setPet((prev) => ({ ...prev, [type]: response.data }));
      setFormState((prev) => ({ ...prev, [type]: {} })); // Formu sıfırla
    } catch (err) {
      console.error(`Error adding ${type}:`, err);
      setError(`Failed to add ${type}. Please try again.`);
    }
  };

  const renderSection = (title, data, fields, endpoint, stateKey) => (
    <>
      <hr />
      <h3>{title}</h3>
      {data && data.length > 0 ? (
        <ul>
          {data.map((item, index) => (
            <li key={index}>{JSON.stringify(item)}</li>
          ))}
        </ul>
      ) : (
        <p>No {title.toLowerCase()} yet.</p>
      )}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleAddRecord(stateKey, endpoint, formState[stateKey]);
        }}
      >
        {fields.map((field) => (
          <div key={field.name}>
            <input
              type={field.type || "text"}
              placeholder={field.placeholder}
              value={formState[stateKey][field.name] || ""}
              onChange={(e) =>
                setFormState((prev) => ({
                  ...prev,
                  [stateKey]: { ...prev[stateKey], [field.name]: e.target.value },
                }))
              }
              required={field.required}
            />
          </div>
        ))}
        <button type="submit">Add {title}</button>
      </form>
    </>
  );

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!pet) return <p>No pet found.</p>;

  return (
    <div className="pet-details">
      <h2>{pet.name}'s Details</h2>
      <p>
        <strong>Species:</strong> {pet.species}
      </p>
      <p>
        <strong>Birth Date:</strong>{" "}
        {pet.birthDate ? new Date(pet.birthDate).toLocaleDateString() : "N/A"}
      </p>
      <hr />
      {renderSection(
        "Health Records",
        pet.healthRecords,
        [
          { name: "date", type: "date", placeholder: "Date", required: true },
          { name: "notes", placeholder: "Notes", required: true },
        ],
        "health-records",
        "healthRecord"
      )}
      {renderSection(
        "Weight History",
        pet.weightHistory,
        [
          { name: "date", type: "date", placeholder: "Date", required: true },
          { name: "weight", type: "number", placeholder: "Weight (kg)", required: true },
        ],
        "weight",
        "weight"
      )}
      {renderSection(
        "Medications",
        pet.medicationRecords,
        [
          { name: "name", placeholder: "Name", required: true },
          { name: "dosage", placeholder: "Dosage", required: true },
          { name: "frequency", placeholder: "Frequency", required: false },
          { name: "startDate", type: "date", placeholder: "Start Date", required: false },
          { name: "endDate", type: "date", placeholder: "End Date", required: false },
          { name: "notes", placeholder: "Notes", required: false },
        ],
        "medications",
        "medication"
      )}
      {renderSection(
        "Appointments",
        pet.appointments,
        [
          { name: "date", type: "date", placeholder: "Date", required: true },
          { name: "time", placeholder: "Time", required: false },
          { name: "reason", placeholder: "Reason", required: false },
          { name: "location", placeholder: "Location", required: false },
        ],
        "appointments",
        "appointment"
      )}
      {renderSection(
        "Emergency Contacts",
        pet.emergencyContacts,
        [
          { name: "name", placeholder: "Name", required: true },
          { name: "relationship", placeholder: "Relationship", required: false },
          { name: "phone", placeholder: "Phone", required: true },
          { name: "address", placeholder: "Address", required: false },
        ],
        "emergency-contact",
        "emergencyContact"
      )}
    </div>
  );
};

export default PetDetails;
